program.behavior:
function onBodyChanged(screen) {
	this.data.menu.LIST.distribute("sync");
}
function onCreate(screen, data, context) {
	this.context = this;
	data.build.call(this, data.BODY);
}
function onItemTap(screen) {
	var data = this.data;
	var menu = data.menu;
	var item = menu.items[menu.selection];
	item.value.call(null, data.BODY, data);
}
function onTouchBegan(container, id, x, y, ticks) {
	var data = this.data;
	var pane = data.menu.PANE;
	if (pane.visible && !pane.hit(x, y)) {
		pane.visible = false;
		data.TOOL.container.active = true;
	}
}

handler.behavior:
function onDescribe(query, selection) {
	return {
		Dialog: DIALOG.Box,
		cancel: "Cancel",
		items: [
			{
				Item: DIALOG.Comment,
				string: query.message,
			},
		],
		variant: 1,
	}
}

container.behavior:
function onTap(container) {
	var pane = this.data.menu.PANE;
	pane.visible = true;
	container.active = false;
}

layout.behavior:
function onMeasureHorizontally(container) {
	var size = container.first.first.measure();
	container.container.previous.coordinates = { left:0, right:0, top:50, bottom:0};
	return size.width;
}
function onMeasureVertically(container) {
	var size = container.first.first.measure();
	return Math.min(size.height, application.height - 70);
}

column.behavior:
function change(container) {
	container.distribute("sync");
}
function onCreate(column, data, context) {
	this.data = data;
	var items = data.items;
	var c = items.length;
	for (var i = 0; i < c; i++) {
		var item = items[i];
		column.add(new item.Item(item, context));
	}
}
function onDisplaying(container) {
	container.distribute("sync");
}
function onItemTap(column, content) {
	var data = this.data;
	var selection = content.index;
	if (data.selection != selection) {
		column.content(data.selection).first.visible = false;
		content.first.visible = true;
		data.selection = selection;
		return false;
	}
	return true;
}

line.behavior:
function onTap(content) {
	var context = this.context;
	this.data.action.call(context, context.data.BODY);
	context.onBodyChanged();
}

function onTap(content) {
	var command = this.data.command;
	var context = this.context;
	command.call(context, context.data.BODY);
	context.data.command = command;
	context.onBodyChanged();
}
function sync(container) {
	container.first.visible = this.context.data.command == this.data.command;
}

container.behavior:
function changeState(container, state) {
	var content = container.first;
	while (content) {
		content.state = state;
		content = content.next;
	}
}
function onCreate(container, data, context) {
	this.data = data;
	this.context = context;
	data.SLIDER = container;
}
function onFinished(container, ticks) {
	var touch = this.touch;
	container.captureTouch(touch.id, touch.x, touch.y, ticks);
	touch.captured = true;
	this.onTouchMoved(container, touch.id, touch.x, touch.y, ticks);
}
function onTouchBegan(container, id, x, y, ticks) {
	this.touch = { captured: false, id: id, x: x, y: y };
	container.duration = 250;
	container.time = 0;
	container.start();
	this.changeState(container, 2);
}
function onTouchCancelled(container, id, x, y, ticks) {
	container.stop();
	this.changeState(container, 1);
}
function onTouchEnded(container, id, x, y, ticks) {
	var touch = this.touch;
	if ((!touch.captured)) {
		container.stop();
		touch.captured = true;
		this.onTouchMoved(container, id, x, y, ticks);
	}
	this.changeState(container, 1);
}
function onTouchMoved(container, id, x, y, ticks) {
	var touch = this.touch;
	if ((!touch.captured) && (Math.abs(x - touch.x) > 8)) {
		container.stop();
		container.captureTouch(id, x, y, ticks);
		touch.captured = true;
	}
	if (touch.captured) {
		var thumb = container.last;
		var bar = thumb.previous;
		var value = this.offsetToValue(x - bar.x - (thumb.width >> 1), bar.width - thumb.width);
		this.data.setter.call(this, value);
		this.sync(container);
	}
}
function offsetToValue(offset, size) {
	var data = this.data;
	var result;
	if (data.log) {
	  var minv = Math.log(data.min);
	  var maxv = Math.log(data.max);
	  result = Math.exp(minv + (offset * (maxv - minv) / size));
	}
	else
		result = data.min + ((offset * (data.max - data.min)) / size);
	if (result < data.min)
		result = data.min;
	else if (result > data.max)
		result = data.max;
	return result;
}
function sync(container) {
	var thumb = container.last;
	var bar = thumb.previous;
	var data = this.data;
	var value = data.getter.call(this);
	thumb.x = bar.x + this.valueToOffset(value, bar.width - thumb.width);
	if (data.step >= 1.0)
		container.first.next.string = Math.round(value).toString();
	else {
		var numStr = value.toString();
		var index = numStr.lastIndexOf(".");
		if (index >= 0)
			numStr = numStr.slice(0, index + 2);
		container.first.next.string = numStr;
	}
}
function valueToOffset(value, size) {
	var data = this.data;
	var result;
	if (data.log) {
		var minv = Math.log(data.min);
		var maxv = Math.log(data.max);
		result = Math.round(((Math.log(value) - minv) * size) / (maxv - minv));
	}
	else
		result = Math.round(((value - data.min) * size) / (data.max - data.min));
	return result;
}

function onCreate(container, data, context) {
	this.data = data;
	this.context = context;
}
function onTabTap(container, content) {
	this.data.setter.call(this, content.index);
	this.context.onBodyChanged();
}
function sync(container) {
	var content = container.last.first;
	var index = 0;
	var selection = this.data.getter.call(this);
	while (content) {
		content.active = index != selection;
		content = content.next;
		index++;
	}
}
